<?php


class wx_small_appLogic
{

    public $appId;
    public $appKey;
    public $appSecret;
    public $mchId;

    public function __construct()
    {
        import('Curl');
        $config = C('small_wx');
        $this->appId = $config['app_id'];
        $this->mchId = $config['mch_id'];
        $this->appKey = $config['key'];
        $this->appSecret = $config['secret'];
    }

    /**
     * @return mixed
     * @throws \ErrorException|\Exception
     */
    public function getAccessToken()
    {
        $cacheKey = 'wx.small-app.access-token';
        $res = rkcache($cacheKey);
        $res = array();
        if (isset($res['access_token']) && !empty($res['access_token']) && isset($res['expires_at']) && $res['expires_at'] - 600 > TIMESTAMP) return $res['access_token'];
        $param = array(
            'grant_type' => 'client_credential',
            'appid' => $this->appId,
            'secret' => $this->appSecret,
        );

        $curl = new Curl();
        $curl->setJsonDecoder(function ($response) {
            return json_decode($response, true);
        });
        $curl->setOpt(CURLOPT_IPRESOLVE, CURL_IPRESOLVE_V4);
        $curl->get('https://api.weixin.qq.com/cgi-bin/token', $param);
        if ($curl->error) {
            throw new \Exception('AccessToken获取失败;Error:' . $curl->errorCode . ': ' . $curl->errorMessage);
        }
        $res = $curl->response;
        $res['expires_at'] = time() + $res['expires_in'];
        wkcache($cacheKey, $res);
        return $res['access_token'];
    }

    public function getQr($page, $scene = '',$default = array())
    {

        $cacheKey = 'wx.small-app.qr.' . $page.'__'.$scene;
        $res = rkcache($cacheKey);
        if ($res) return $res;
        $param = array(
            'scene' => $scene,
            'page' => $page,
        );
        $accessToken = empty($default)? $this->getAccessToken():$default['accesstoken'];
        $curl = new Curl();
        $curl->setHeader('Content-type','application/json;charset=\'utf-8\'');
        /*$curl->setJsonDecoder(function ($response) {
            return json_decode($response, true);
        });*/
        $curl->setOpt(CURLOPT_IPRESOLVE, CURL_IPRESOLVE_V4);
        $curl->post('https://api.weixin.qq.com/wxa/getwxacodeunlimit?access_token=' . $accessToken, $param);
        if ($curl->error) {
            throw new \Exception('二维码获取失败;Error: ' . $curl->errorCode . ': ' . $curl->errorMessage);
        }
        $res = $curl->response;
        if($res->errcode&&$res->errcode>0){
            throw new \Exception('二维码获取失败;错误信息: ' . $res->errmsg);
        }
        wkcache($cacheKey, $res);
        return $res;
    }

    public function getQrHttp($page,$scene = '', $default_image = 'https://wxapi.hangomart.com/data/upload/mobile/special/s900000001/s900000001_06414062670791785.jpg') {
        $cacheKey = 'wx.small-app.http.qr.' . $page.'__'.$scene;
        $res = rkcache($cacheKey);
        if ($res && str_replace(strpos($res,'hangowa.com') === false)) return $res;
        if (file_exists(BASE_UPLOAD_PATH.DS.ATTACH_COMMON.DS.'shequ_' . md5($page.$scene) . '.jpg')) {
            $image_url = WEI_UPLOAD_URL.DS.ATTACH_COMMON.DS.'shequ_'.md5($page.$scene).'.jpg';
            wkcache($cacheKey, $image_url);
            return $image_url;
        }
        $param = array(
            'scene' => $scene,
            'page' => $page,
        );
        $accessToken = $this->getAccessToken();
        $curl = new Curl();
        $curl->setHeader('Content-type','application/json;charset=\'utf-8\'');
        $curl->setOpt(CURLOPT_IPRESOLVE, CURL_IPRESOLVE_V4);
        $curl->post('https://api.weixin.qq.com/wxa/getwxacodeunlimit?access_token=' . $accessToken, $param);
        if ($curl->error) {
            return $default_image;
            //throw new \Exception('二维码获取失败;Error: ' . $curl->errorCode . ': ' . $curl->errorMessage);
        }
        $res = $curl->response;
        if($res->errcode&&$res->errcode>0){
            return $default_image;
            //throw new \Exception('二维码获取失败;错误信息: ' . $res->errmsg);
        }
        $qr_file_image = BASE_UPLOAD_PATH.DS.ATTACH_COMMON.DS.'shequ_' . md5($page.$scene) . '.jpg';
        $result_one = file_put_contents($qr_file_image, $res);
        if (!$result_one) {
            return $default_image;
        }
        $image_url = WEI_UPLOAD_URL.DS.ATTACH_COMMON.DS.'shequ_'.md5($page.$scene).'.jpg';
        wkcache($cacheKey, $image_url);
        return $image_url;
    }

    public function getQrBase64($page, $scene = '') {
        $cacheKey = 'wx.small-app.64.qr.' . $page.'__'.$scene;
        $res = rkcache($cacheKey);
        if ($res) return $res;
        $default_base64_string = 'data:image/jpeg;base64,';
        $param = array(
            'scene' => $scene,
            'page' => $page,
        );
        $accessToken = $this->getAccessToken();
        $curl = new Curl();
        $curl->setHeader('Content-type','application/json;charset=\'utf-8\'');
        $curl->setOpt(CURLOPT_IPRESOLVE, CURL_IPRESOLVE_V4);
        $curl->post('https://api.weixin.qq.com/wxa/getwxacodeunlimit?access_token=' . $accessToken, $param);
        if ($curl->error) {
            return $default_base64_string;
            //throw new \Exception('二维码获取失败;Error: ' . $curl->errorCode . ': ' . $curl->errorMessage);
        }
        $res = $curl->response;
        if($res->errcode&&$res->errcode>0){
            return $default_base64_string;
            //throw new \Exception('二维码获取失败;错误信息: ' . $res->errmsg);
        }
        $type = getimagesizefromstring($res)['mime'];
        $base64String = 'data:' . $type . ';base64,' . chunk_split(base64_encode($res));
        wkcache($cacheKey, $base64String);
        return $base64String;
    }

    //推送小程序消息
    public function push_template_msg($params) {

        $push_url = 'https://api.weixin.qq.com/cgi-bin/message/wxopen/template/send?access_token=';
        $access_token = $this->getAccessToken();
        $push_url = $push_url. $access_token;

        $post_data = array(
            'touser' => $params['open_id'],
            'template_id' => $params['template_id'],
            'page' => $params['page'],// 点击模板卡片后的跳转页面，仅限本小程序内的页面。支持带参数,（示例index?foo=bar）。该字段不填则模板无跳转。
            'form_id' => $params['form_id'],
            'data'   => $params['keywords'],
        );

        $curl = new Curl();
        $curl->setHeader('Content-type','application/json;charset=\'utf-8\'');
        $curl->setOpt(CURLOPT_IPRESOLVE, CURL_IPRESOLVE_V4);
        $result = $curl->post($push_url, json_encode($post_data));
        if(is_string($result)) {
            $result = json_decode($result ,true);
        }else{
            $result = json_decode(json_encode($result) ,true);
        }
        if ($result['errcode'] != 0) {
            throw new \Exception('推送小程序消息失败;错误信息: ' . $result['errmsg'] . '错误码是：'. $result['errcode']);
        }
        return true;

    }

}
